const SUPABASE_URL = $env.SUPABASE_URL.replace(/\/$/, '');
const SUPABASE_KEY = $env.SUPABASE_SERVICE_ROLE_KEY;
const TABLE = 'csf_category_embeddings';

function cosineSim(a, b) {
  if (typeof b === 'string') {
    try {
      b = JSON.parse(b);
    } catch (e) {
      throw new Error(`Failed to parse category embedding: ${b}`);
    }
  }

  if (!Array.isArray(a) || !Array.isArray(b)) {
    throw new Error(`Invalid input to cosineSim. a or b not an array.`);
  }

  const dot = a.reduce((sum, ai, i) => sum + ai * b[i], 0);
  const magA = Math.sqrt(a.reduce((sum, ai) => sum + ai * ai, 0));
  const magB = Math.sqrt(b.reduce((sum, bi) => sum + bi * bi, 0));
  return dot / (magA * magB);
}

// Fetch the 22 CSF category embeddings from Supabase
const categories = await this.helpers.httpRequest({
  method: 'GET',
  url: `${SUPABASE_URL}/rest/v1/${TABLE}?select=id,embedding`,
  headers: {
    apikey: SUPABASE_KEY,
    Authorization: `Bearer ${SUPABASE_KEY}`
  },
  json: true
});

const results = [];

