  const chunkEmbedding = item.json.embedding;
  const category_scores = {};

  for (const cat of categories) {
    const score = cosineSim(chunkEmbedding, cat.embedding);
    category_scores[cat.id] = Math.round(score * 1000);
  }

  results.push({
    json: {
      ...item.json,
      category_scores
    }
  });
}

