const pdfPath = path.join(tmpDir, pdfFile);
const pdfBuffer = await fs.promises.readFile(pdfPath);

return [{
  json: {
    pdf_path: pdfPath,
    output_dir: tmpDir,
    base_name: path.basename(pdfFile, '.pdf'),
  },
  binary: {
    data: {
      data: pdfBuffer.toString('base64'),
      mimeType: 'application/pdf',
      fileName: pdfFile,
    }
  }
}];
